import './WeatherCard.css';
import React from "react";

class WeatherCard extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            temperature : null,
            cloudCover : null,
            humidity : null,
            precipitation : null,
            uvIndex : null,
            visibility : null,
            weatherCode: null,
            weatherDescription: null,
            realCity: "Nowhere"
        }
        this.updateCard = this.updateCard.bind(this);
    }

    updateCard(currData, realCity) {
        this.setState({
            temperature : currData[0]["temp_F"],
            cloudCover : currData[0]["cloudcover"],
            humidity : currData[0]["humidity"],
            precipitation : currData[0]["precipInches"],
            uvIndex : currData[0]["uvIndex"],
            visibility : currData[0]["visibilityMiles"],
            weatherCode: currData[0]["weatherCode"],
            realCity: realCity
        });
        fetch("https://wttr.in/" + city + "?format=j1")
            .then(response => response.json())
            .then(data => {
                const currData = data["current_condition"];
                const areaData = data["nearest_area"];
                const realCity = areaData[0]["areaName"][0]["value"] + ", " + areaData[0]["region"][0]["value"] + ", " + areaData[0]["country"][0]["value"];
                this.card.current.updateCard(currData, realCity);
            })
