import './App.css';
import React from "react";
import WeatherCard from './WeatherCard.js'


class App extends React.Component {


    constructor(props) {
        super(props);
        this.card = null;
        this.state = {
            city: "test"
        }
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleSubmit(event) {
        event.preventDefault();
        let data = new FormData(event.target);
        this.setState({city : data.get("city")});
        document.getElementById('cityInput').value = "";
        this.fetchCityWeather();
    }

    fetchCityWeather() {
        console.log(
            fetch("https://wttr.in/" + this.state.city + "?format=j1")
                .then(response => response.json())
                .then(data => {
                    const currData = data["current_condition"];

                    this.card.setState({
                        temperature : currData["temp_F"],
                        cloudCover : currData["cloudcover"],
                        humidity : currData["humidity"],
                        precipitation : currData["precipInches"],
                        uvIndex : currData["uvIndex"],
                        visibility : currData["visibilityMiles"],
                        weatherCode: currData["weatherCode"],
                        weatherDescription: currData[""]
                    });
                })
        );
    }

    render() {
        return (
            <div className="App">
                <h1>{this.state.city}</h1>
                <form onSubmit={this.handleSubmit}>
                    <label>City:
                        <input id="cityInput" name="city" type="text" />
                    </label>
                    <input type="submit" value="Submit" />
                </form>
                <WeatherCard ref={this.card} />
            </div>
        );
    }
}

export default App;
